  }
  return mid+"";
}

function getRequestId(){
  return "RID"+new Date().getTime()+getMid();
}


this.receive=function(service,device){
  var identifier=service.identifier;
  var result= [{
    "productKey":service.productKey,
    "deviceName":service.deviceName,
    "mid":service.mid,
    "type":"service",
    "identifier":identifier+"_reply",
    "data":{},
    "code":0
  }]

  if(service.type=="property" && (identifier=="get" || identifier=="set")){
	result.push({
	  "mid":getRequestId(),
	  "productKey":device.productKey,  
	  "deviceName":device.deviceName,
	  "type":"property",
	  "identifier":"report",
	  "occurred":new Date().getTime(),
	  "time":new Date().getTime(),
	  "data":service.params
	});
  }

  return result;
}

this.report=function(device){
  return {
    "mid":getRequestId(),
    "productKey":device.productKey,  
    "deviceName":device.deviceName,
    "type":"property",
    "identifier":"report",
    "occurred":new Date().getTime(),	//时间戳，设备上的事件或数据产生的本地时间
    "time":new Date().getTime(),		//时间戳，消息上报时间
    "data":{
      "rssi":127-parseInt(Math.random()*127),
	  "powerstate":Math.random()>0.5?1:0
    }
  }
}
